import React, { useState, useEffect } from "react";
import { makeStyles } from "@material-ui/core/styles";
import ImageMapper from "react-image-mapper";
import LectureRoomModal from "../components/LectureRoomModal";
import { Modal } from "antd";

const useStyles = makeStyles({
  root: {},
  ["@media (max-width:1520px)"]: {
    map: {
      display: "none"
    }
  }
});

const lectureRoom = {
  name: "Swot-map",
  areas: [
    {
      name: "1",
      shape: "circle",
      coords: [445, 285, 18],
      preFillColor: "red",
      fillColor: "blue"
    },
    {
      name: "2",
      shape: "circle",
      coords: [400, 220, 18],
      preFillColor: "orange",
      fillColor: "blue"
    },
    {
      name: "3",
      shape: "circle",
      coords: [520, 245, 18],
      preFillColor: "yellow",
      fillColor: "blue"
    },
    {
      name: "5",
      shape: "circle",
      coords: [420, 495, 18],
      preFillColor: "green",
      fillColor: "blue"
    },
    {
      name: "6",
      shape: "circle",
      coords: [440, 130, 18],
      preFillColor: "blue",
      fillColor: "purple"
    }
  ]
};

const SWOTMap = () => {
  const [Lectureroom, setLectureroom] = useState("미가엘관");
  const [visible, setVisible] = useState(true);

  const handleCancel = () => {
    setVisible(false);
  };

  const handleOk = () => {
    setVisible(false);
  };

  const showModal = () => {
    console.log(visible);
    <div>
      <Modal title="강의실 정보" onOk={handleOk} onCancel={handleCancel}>
        <p>{Lectureroom}</p>
      </Modal>
    </div>;
  };

  const classes = useStyles();

  const imageClick = (area, event) => {
    showModal();
    console.log(event);
    console.log(area);
    console.log(area.name);
  };

  const mapView = (
    <ImageMapper
      src="static/images/SWOTMAP.png"
      width={600}
      height={400}
      imgWidth={500}
      map={lectureRoom}
      onClick={showModal}
    />
  );

  return (
    <>
      <div>
        {/* {modalstatus === true ? <LectureRoomModal /> : ""} */}
        {mapView}
      </div>
    </>
  );
};

export default SWOTMap;
